mapper.select:
SELECT BOARD_ID, BOARD_TITLE, BOARD_CATEGORY, BOARD_DATE, BOARD_UPDATE, BOARD_CONTENT, BOARD_OFFENSIVE, MEMBER_ID
FROM BOARD
WHERE BOARD_CATEGORY = #{boardCategory}
  AND BOARD_DATE >= SYSDATE - 1
  AND BOARD_OFFENSIVE = 'F';

SELECT BAORD_ID, BOARD_TITLE, BOARD_CATEGORY, BOARD_DATE, BOARD_UPDATE, BOARD_CONTENT, BOARD_OFFENSIVE, MEMBER_ID
FROM BOARD
WHERE BOARD_ID = #{boardId};
